<style lang="scss">
    @import "../styles/variables.scss";

    $inactiveColor: rgba(255, 255, 255, 0.705);
    $activeColor: white;

    .nav-link
    {
        display: flex;
        justify-content: center;
        align-items: center;

        color: $inactiveColor;
        transition: color 0.4s ease-out;
        padding: 0 0.75em 0 0;
        text-decoration: none;
        cursor: pointer;

        &:not(:first-child)
        {
            padding: 0 0.75em;
        }

        &:hover { color: $activeColor; }
        &:hover svg path,
        &.active svg path
        {
            fill: $activeColor;
        }

        &.active
        {
            font-weight: bold;
            color: $activeColor;
        }
    }

    span
    {
        margin-top: -2px;
    }

    svg
    {
        height: $navHeight;

        path
        {
            transition: fill 0.4s ease-out;
            fill: $inactiveColor;
        }
    }
</style>

<script>
    export let segment;
    export let link;
</script>

